function swap(nums, i, j) {
  const temp = nums[i];
  nums[i] = nums[j];
  nums[j] = temp;
}

function maxHeapify(nums, i, length) {
  let left = 2 * i + 1;
  let right = 2 * i + 2;

  let largest = i;

  if (left = 0; i -= 1) {
    maxHeapify(nums, i, nums.length);
  }
}

function heapsort(nums) {
  let length = nums.length;

  for (let i = nums.length - 1; i >= 0; i -= 1) {
    swap(0, i);
    length -= 1;
    maxHeapify(nums, 0, length);
  }
}

